{
    "request": {
        "type": "config",
        "subType": "usertype_v2",
        "action": "get",
        "component": "app",
        "framework": "*",
        "rootOrgId": "*",
        "data": {
            "templateName": "usertype_v2",
            "action": "get",
            "fields": [
                {
                    "code": "teacher",
                    "name": "Teacher",
                    "formConfig": {
                        "request": {
                            "type": "profileConfig",
                            "subType": "default",
                            "action": "get"
                        },
                        "url": "/api/data/v1/form"
                    },
                    "translations": "{\"en\":\"Teacher\",\"as\":\"শিক্ষক\",\"bn\":\"শিক্ষক\",\"gu\":\"શિક્ષક\",\"hi\":\"शिक्षक\",\"kn\":\"ಶಿಕ್ಷಕ/ಕಿ\",\"mr\":\"शिक्षक\",\"or\":\"ଶିକ୍ଷକ\",\"pa\":\"ਅਧਿਆਪਕ\",\"ta\":\"ஆசிரியர்\",\"te\":\"ఉపాధ్యాయుడు\",\"ur\":\"استاد\"}",
                    "image": "ic_teacher.svg",
                    "ambiguousFilters": [
                        "teacher",
                        "instructor"
                    ],
                    "searchFilter": [
                        "Teacher",
                        "Instructor"
                    ],
                    "attributes": {
                        "mandatory": [
                            "board",
                            "medium",
                            "gradeLevel"
                        ],
                        "optional": [
                            "subject"
                        ]
                    }
                },
                {
                    "code": "student",
                    "name": "Student",
                    "formConfig": {
                        "request": {
                            "type": "profileConfig",
                            "subType": "default",
                            "action": "get"
                        },
                        "url": "/api/data/v1/form"
                    },
                    "translations": "{\"en\":\"Student\",\"as\":\"শিক্ষাৰ্থী\",\"bn\":\"ছাত্র\",\"gu\":\"વિદ્યાર્થી\",\"hi\":\"विद्यार्थी\",\"kn\":\"ವಿದ್ಯಾರ್ಥಿ\",\"mr\":\"विद्यार्थी\",\"or\":\"ଛାତ୍ର ଛାତ୍ରୀ\",\"pa\":\"ਵਿਦਿਆਰਥੀ\",\"ta\":\"மாணவர்\",\"te\":\"విద్యార్థి\",\"ur\":\"طالب علم\"}",
                    "image": "ic_student.svg",
                    "ambiguousFilters": [
                        "learner",
                        "student"
                    ],
                    "searchFilter": [
                        "Student",
                        "Learner"
                    ],
                    "attributes": {
                        "mandatory": [
                            "board",
                            "medium",
                            "gradeLevel"
                        ],
                        "optional": [
                            "subject"
                        ]
                    }
                },
                {
                    "code": "administrator",
                    "name": "HT & Officials",
                    "formConfig": {
                        "request": {
                            "type": "profileConfig",
                            "subType": "admin",
                            "action": "get"
                        },
                        "url": "/api/data/v1/form"
                    },
                    "translations": "{\"en\":\"HT & Officials\",\"as\":\"বিদ্যালয়ৰ মুৰব্বী বা বিষয়াসকল\",\"bn\":\"বিদ্যালয়ের প্রধান বা কর্মকর্তা\",\"gu\":\"શાળાના વડા અથવા અધિકારીઓ\",\"hi\":\"स्कूल प्रमुख या अधिकारी\",\"kn\":\"ಶಾಲಾ ಮುಖ್ಯಸ್ಥರು ಅಥವಾ ಅಧಿಕಾರಿಗಳು\",\"mr\":\"शाळा प्रमुख किंवा अधिकारी\",\"or\":\"ସ୍କୁଲ ମୁଖ୍ୟ ଓ ଅଧିକାରୀ\",\"pa\":\"ਸਕੂਲ ਮੁਖੀ ਜਾਂ ਅਧਿਕਾਰੀ\",\"ta\":\"தலைமை ஆசிரியர் & அதிகாரிகள்\",\"te\":\"స్కూల్ హెడ్ లేదా అధికారులు\",\"ur\":\"اسکول سربراہ یا عہدیدار\"}",
                    "image": "ic_admin.svg",
                    "ambiguousFilters": [],
                    "searchFilter": [
                        "administrator"
                    ],
                    "attributes": {
                        "mandatory": [
                            "board"
                        ],
                        "optional": []
                    }
                },
                {
                    "code": "parent",
                    "name": "Parent",
                    "formConfig": {
                        "request": {
                            "type": "profileConfig",
                            "subType": "default",
                            "action": "get"
                        },
                        "url": "/api/data/v1/form"
                    },
                    "translations": "{\"en\":\"Parent\"}",
                    "image": "ic_parent.svg",
                    "ambiguousFilters": [],
                    "searchFilter": [],
                    "attributes": {
                        "mandatory": [
                            "board",
                            "medium",
                            "gradeLevel"
                        ],
                        "optional": [
                            "subject"
                        ]
                    }
                }
            ]
        }
    }
}

